import java.util.ArrayList; import java.util.Arrays; import java.util.List; // Solution1 forå¾ªç¯ä¸­éå†ä¸­æ’åºï¼Œæ—¶é—´å¤æ‚åº¦å¤ªé«˜ class Solution1 { public List&lt;List&gt; subsetsWithDup(int[] nums) { List&lt;List&gt; subsets = new ArrayList&lt;&gt;(); for(int k = 0; k&lt;= nums.length; k++){ dfs(nums,0,k,new ArrayList&lt;&gt;(),subsets); } return subsets; }
public void dfs(int[] nums, int begin, int k, List&lt;Integer&gt; cur , List&lt;List&lt;Integer&gt;&gt; subsets ){ if(k == cur.size()){ //donnot collect dep subset if(isContainTargeList(subsets, new ArrayList&lt;&gt;(cur))){ return; } subsets.add(new ArrayList&lt;&gt;(cur)); return; } for(int i =begin; i &lt;nums.length - (k - cur.size()-1); i++){ cur.add(nums[i]); dfs(nums, i+1, k, cur, subsets); cur.remove(cur.size()-1); } } public boolean isContainTargeList(List&lt;List&lt;Integer&gt;&gt; subsets, List&lt;Integer&gt; targeList ){ //å› ä¸ºæ˜¯ç»„åˆï¼Œä¸è€ƒè™‘å…ƒç´ é¡ºåºï¼Œæ‰€æœ‰éœ€è¦éå†subsets,åˆ¤æ–­æ˜¯å¦æ˜¯å¦åŒ…å«ä¸targeListç›¸åŒçš„ç»„åˆ for(int i = 0; i&lt; subsets.size();i++){ Collections.sort(subsets.get(i)); Collections.sort(targeList); if(subsets.get(i).equals(targeList)){ return true; } } return false; } }
class Solution { List&lt;List&gt; subsets = new ArrayList&lt;&gt;(); List path = new ArrayList&lt;&gt;();
public List&lt;List&lt;Integer&gt;&gt; subsetsWithDup(int[] nums) { Arrays.sort(nums); dfs(nums,0); return subsets; } public void dfs(int[] nums, int begin){ subsets.add(new ArrayList&lt;&gt;(path)); for(int i = begin; i&lt; nums.length; i++ ){ //æ ‘çš„åŒä¸€å±‚ï¼Œç›¸åŒå…ƒç´ éœ€è¦å»é‡ï¼Œç›´æ¥è·³è¿‡ if(i &gt; begin &amp;&amp; nums[i] == nums[i-1]){ continue; } path.add(nums[i]); dfs(nums, i+1); path.removeLast(); } } }
